function procesarContextoUsuario(userData) {
  if (!userData || !userData.telegram_id) {
    return {
      esNuevo: true,
      estadoActual: "nuevo",
      mensajeSistema: "Usuario nuevo detectado, iniciando bienvenida",
      accionesPermitidas: ["bienvenida", "registro"]
    };
  }

  const ultimaAccion = userData.last_action || "desconocida";
  const estadoConversacion = userData.conversation_state || "activa";
  let contextoSesion;

  // Intentar parsear session_context si existe
  try {
    contextoSesion = typeof userData.session_context === 'string'
      ? JSON.parse(userData.session_context)
      : (userData.session_context || {});
  } catch (e) {
    contextoSesion = {};
  }

  // Variables para controlar el mensaje del sistema
  let estadoActual;
  let mensajeSistema;
  let accionesPermitidas;
  let recomendacionAgente = "";

  // Determinar el estado actual basado en conversation_state y last_action
  if (estadoConversacion === "pendiente_pago") {
    estadoActual = "pendiente_pago";
    mensajeSistema = "El usuario tiene un pedido pendiente de pago. Ofrece completar el pago o modificar el pedido.";
    accionesPermitidas = ["pago", "modificar_pedido"];
    recomendacionAgente = "Al identificar usuario con pago pendiente, ofrecer opci√≥n de completar pago o hacer cambios";
  } else if (ultimaAccion === "cre√≥_pedido" || ultimaAccion === "pag√≥_pedido") {
    estadoActual = "pedido_completado";
    mensajeSistema = "El usuario tiene un pedido completado. Est√° disponible para realizar nuevas acciones.";
    accionesPermitidas = ["nuevo_pedido", "consultar"];
    recomendacionAgente = "Saludar como cliente recurrente y ofrecer nuevo pedido o consulta";
  } else if (estadoConversacion === "en_proceso") {
    estadoActual = "creando_pedido";
    mensajeSistema = "Usuario en proceso de crear un pedido. Continuar recopilando informaci√≥n faltante.";
    accionesPermitidas = ["continuar_pedido"];
    recomendacionAgente = "Continuar desde el punto donde qued√≥ la recopilaci√≥n de datos";
  } else if (ultimaAccion === "iniciando_pago") {
    estadoActual = "proceso_pago";
    mensajeSistema = "Usuario iniciando proceso de pago. Redirigir al flujo de pagos.";
    accionesPermitidas = ["procesar_pago"];
    recomendacionAgente = "Dirigir inmediatamente al agente de pagos";
  } else {
    estadoActual = "conversacion_general";
    mensajeSistema = "Conversaci√≥n general, ofrecer ayuda y opciones disponibles.";
    accionesPermitidas = ["iniciar_pedido", "consultar"];
    recomendacionAgente = "Ofrecer men√∫ de opciones principales";
  }

  return {
    esNuevo: false,
    ultimaAccion,
    estadoConversacion,
    contextoSesion,
    estadoActual,
    mensajeSistema,
    accionesPermitidas,
    recomendacionAgente,
    datosCompletos: userData
  };
}

