const nowUTC = new Date();
// Ajustar a hora de Chile (UTC-4)
const chileanOffset = -4 * 60; // -4 horas en minutos
const now = new Date(nowUTC.getTime() + (chileanOffset + nowUTC.getTimezoneOffset()) * 60000);

// Obtenemos d√≠a y hora actuales
const hora = now.getHours();
const diaSemana = now.getDay(); // 0 = domingo, 6 = s√°bado
const HORA_CORTE_ALMUERZO = 9; // 9:00 AM

// Funci√≥n para a√±adir d√≠as a una fecha
function addDays(date, days) {
  const result = new Date(date);
  result.setDate(result.getDate() + days);
  return result;
}

// Funci√≥n para formatear fecha como DD/MM/YYYY
function formatDate(date) {
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  return `${day}/${month}/${year}`;
}

// Calculamos la fecha de entrega y el motivo
let fechaEntregaDisponible;
let motivoFecha = "";

// L√≥gica para determinar fecha de entrega
if (diaSemana === 0) { // Domingo
  if (hora < HORA_CORTE_ALMUERZO) {
    fechaEntregaDisponible = addDays(now, 1); // Lunes
    motivoFecha = "Su pedido est√° dentro del horario de corte dominical, la entrega ser√° para ma√±ana lunes.";
  } else {
    fechaEntregaDisponible = addDays(now, 2); // Martes
    motivoFecha = `Por ser domingo despu√©s de las ${HORA_CORTE_ALMUERZO}:00 horas, la entrega ser√° para el martes.`;
  }
} else if (diaSemana === 6) { // S√°bado
  fechaEntregaDisponible = addDays(now, 2); // Lunes
  motivoFecha = "Por ser s√°bado, la entrega ser√° para el lunes.";
} else if (hora >= HORA_CORTE_ALMUERZO) {
  // D√≠a de semana despu√©s de las 9 AM
  fechaEntregaDisponible = addDays(now, 2);

  // Ajustamos si cae en fin de semana
  const nuevoDiaSemana = fechaEntregaDisponible.getDay();
  if (nuevoDiaSemana === 6) { // S√°bado
    fechaEntregaDisponible = addDays(fechaEntregaDisponible, 2);
  } else if (nuevoDiaSemana === 0) { // Domingo
    fechaEntregaDisponible = addDays(fechaEntregaDisponible, 1);
  }

  motivoFecha = `Por ser despu√©s de las ${HORA_CORTE_ALMUERZO}:00 horas, la entrega ser√° en dos d√≠as h√°biles.`;
} else {
  // D√≠a de semana antes de las 9 AM
  fechaEntregaDisponible = addDays(now, 1);
  motivoFecha = "Su pedido est√° dentro del horario de corte, la entrega ser√° para ma√±ana.";
}

// Crear el objeto de resultado simplificado
