    mes: now.getMonth() + 1,
    nombre_mes: ["enero", "febrero", "marzo", "abril", "mayo", "junio", "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"][now.getMonth()],
    es_finde: diaSemana === 0 || diaSemana === 6,
    hora_numerica: hora
  },
  disponibilidad_entrega: {
    fecha_entrega: formatDate(fechaEntregaDisponible),
    hora_entrega: "12:00:00",
    fecha_hora_entrega: fechaEntregaDisponible.toISOString(),
    texto_fecha: `${["domingo", "lunes", "martes", "mi√©rcoles", "jueves", "viernes", "s√°bado"][fechaEntregaDisponible.getDay()]} ${fechaEntregaDisponible.getDate()} de ${["enero", "febrero", "marzo", "abril", "mayo", "junio", "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"][fechaEntregaDisponible.getMonth()]}`,
    motivo: motivoFecha,
    dias_espera: Math.round((fechaEntregaDisponible - now) / (1000 * 60 * 60 * 24))
  },
  validacion_pago: {
    formato_fecha_esperado: "DD/MM/YYYY",
    formato_hora_esperado: "HH:mm:ss",
    fecha_minima_valida: formatDate(addDays(now, -1)),
    fecha_maxima_valida: formatDate(addDays(now, 1))
