  const hoy = new Date();
  const primerDia = new Date(hoy);

  // Ajustar al lunes pr√≥ximo
  const diaSemanaActual = hoy.getDay(); // 0 = domingo, 1 = lunes, etc.
  const diasHastaLunes = diaSemanaActual === 0 ? 1 : diaSemanaActual === 6 ? 2 : 8 - diaSemanaActual;
  primerDia.setDate(hoy.getDate() + diasHastaLunes);

  // Generar fechas para cada d√≠a de la pr√≥xima semana
  diasSemana.forEach((dia, index) => {
